package leetcode.arrayNdstrings;

/**
 * Needle in haystack
 */

public class FindSubstring {
   /**
    *
    *  Brute force algorithm with O(n)^2 time complexity.
    *
     * str and sub consist of only lower-case English characters.
     * @param str 0 <= str.length
     * @param sub sub.length <= 5 * 104
     * @return
     */
    public int strStr(String str, String sub) {
        if(sub==null || sub.length() ==0)
            return 0;

        final char[] l = str.toCharArray();
        final char[] s = sub.toCharArray();

        int firstIndex = -1;
        //substring can't be found in smaller main string.
        if(s.length>l.length)
            return firstIndex;

        for (int i = 0; i < l.length; i++) {
            if(Character.toString(l[i]).equals(Character.toString(s[0]))){
                //temporally update firstIndex to where we encounter first match.
                firstIndex=i;
                int ix =i, sx=0;
                while(ix < l.length && sx< s.length){
                    //If unmatched char found in main String
                    if (!(Character.toString(l[ix++]).equals(Character.toString(s[sx++])))) {
                        //reset firstIndex to -1 again.
                        firstIndex = -1;
                        break;
                    }
                }
                //If while existed with found match and substring traversal is already finished then
                // we can confirm match.
                if(firstIndex!=-1 && sx==s.length) {
                    return firstIndex;
                }
                //else reset firstIndex to mark no match found.
                else{
                    firstIndex=-1;
                }
            }
            //Reducing worst case complexity: If rest of main string length is smaller then substring than substring can't be found.
            if(((l.length-(i+1)) < s.length ) && firstIndex==-1){
                return firstIndex;
            }
        }

        return firstIndex;
    }

    public static void main(String[] args) {
        FindSubstring findSubstring = new FindSubstring();
        String a =
                "";
        String b =
                "";
//        int i = findSubstring.strStr("hello", "ll");
//        System.out.println(i);
        int i = findSubstring.strStr(a, b);
        System.out.println("Result: " + i);
    }
}
